function get_post_response($url,$fields_string) {
    
    //open connection
    $ch = curl_init();

    //set the url, number of POST vars, POST data
    curl_setopt($ch,CURLOPT_URL, $url);
    curl_setopt($ch,CURLOPT_RETURNTRANSFER, true);        
    curl_setopt($ch,CURLOPT_POSTFIELDS, $fields_string);

    //execute post
    $result = curl_exec($ch);

    //close connection
    curl_close($ch);
    
    return $result;
        
}





function get_rep_data($file_path) {
    
    global $run_environment;
    global $max_records;

    
    // THIS DOESNT WORK!        
    // $html = file_get_contents($file_path);
    // $dom = new simple_html_dom();
    // $dom->load($html);    
    // $table = $dom->find("tr", 0);
    // echo $table->plaintext;    
    
    
    // THIS WORKS!    
    
    $html = file_get_contents($file_path); 

    //Make sure we got the file, use backup if needed
    if(!$html) {
        $html = scraperWiki::scrape('http://dropbox.ashlock.us/opengov/democracymap/city_ma.xls'); 
    }

   
    $dom = new domDocument;   
    $dom->loadHTML($html);


    $rowcount = 1;
    foreach ($dom->getElementsByTagName("tr") as $result) {
    
        $column_count = 0;
        
        $cell = null;
        foreach ($result->childNodes as $row) {
            
            $value = null;
            $value = trim(utf8_decode($row->nodeValue));
            
            // If this is the first row
            if($rowcount == 1) {
                if($value) {
                    $headings[] = strtolower(str_replace(' ', '_', $value));
                }
            }
            
            if($rowcount > 1) {
                if($value) {
                    $columns[$headings[$column_count]] = $value;                
                }
            }
            
            
            $column_count++;
        }
        
        
        // If this is the first row
        if($rowcount == 1) {        
            $rowcount++;
            continue;
        }        

        if ($run_environment == 'dev') {
            $reps[] = $columns;
        }
        else {
            scraperwiki::save_sqlite(array('jurisdiction','last_name', 'first_name'), $columns);    
        }        

        $rowcount++;
        if ($run_environment == 'dev' && $rowcount > $max_records) break;

    }
    

    if ($run_environment == 'dev') {
        return $reps;
    }
    else {
        return true;

    }


}












